@namespace
class SpriteKind:
    coin = SpriteKind.create()
    flower = SpriteKind.create()
    fireball = SpriteKind.create()

def on_overlap_tile(sprite, location):
    game.over(False, effects.bubbles)
scene.on_overlap_tile(SpriteKind.player, myTiles.tile2, on_overlap_tile)

def on_overlap_tile2(sprite, location):
    game.over(True, effects.star_field)
scene.on_overlap_tile(SpriteKind.player,
    sprites.food.small_burger,
    on_overlap_tile2)

def on_overlap_tile3(sprite, location):
    global current_level
    current_level += 1
    startLevel()
scene.on_overlap_tile(SpriteKind.player, myTiles.tile4, on_overlap_tile3)

def on_a_pressed():
    if Franklin.vy == 0:
        Franklin.vy = -120
        music.jump_up.play()
controller.A.on_event(ControllerButtonEvent.PRESSED, on_a_pressed)

def on_overlap_tile4(sprite, location):
    global current_level
    current_level += 1
    startLevel()
scene.on_overlap_tile(SpriteKind.player, myTiles.tile8, on_overlap_tile4)

def on_on_overlap(sprite, otherSprite):
    music.ba_ding.play()
    info.change_score_by(1)
    otherSprite.destroy()
sprites.on_overlap(SpriteKind.player, SpriteKind.coin, on_on_overlap)

